// Pega todos os inputs do node anterior
const previousItems = $input.all();

// Pega a saída bruta do AI Agent
let aiOutputRaw = previousItems[0]?.json?.output || "";
aiOutputRaw = aiOutputRaw.trim();

// Remove blocos markdown ```json ... ```
aiOutputRaw = aiOutputRaw
    .replace(/```json/gi, "")
    .replace(/```/g, "")
    .trim();

// Tenta converter diretamente para JSON, caso o modelo já tenha obedecido
let parsed = null;
try {
    parsed = JSON.parse(aiOutputRaw);
} catch (err) {
    parsed = null;
}

// Se já veio JSON válido, simplesmente retorna formatado
if (parsed && typeof parsed === "object") {
    return [
        {
            json: {
                translated: parsed.translated || "Sem tradução",
                explanation: parsed.explanation || "Sem explicação",
                timestamp: new Date().toISOString()
            }
        }
    ];
}

// -----------------------------
//   TRATAMENTO PARA TEXTO BRUTO
// -----------------------------

// Usa regex para dividir frases corretamente sem quebrar abreviações
const sentenceRegex = /(?<=[^.]\.|\?|!)\s+(?=[A-ZÀ-Ú])/;

let sentences = aiOutputRaw.split(sentenceRegex).map(s => s.trim()).filter(s => s);

// Se nada foi encontrado (texto muito curto ou sem pontuação)
if (sentences.length === 0) {
    return [
        {
            json: {
                translated: aiOutputRaw,
                explanation: "",
                timestamp: new Date().toISOString()
            }
        }
    ];
}

// Primeira frase = tradução
const translatedText = sentences[0];

// Restante = explicação
const explanationText = sentences.slice(1).join(" ").trim();

// Monta o JSON final
const output = {
    translated: translatedText || "Sem tradução",
    explanation: explanationText || "Sem explicação",
    timestamp: new Date().toISOString()
};

// Retorno padrão do n8n
return [
    { json: output }
];
